import "./IERC20Extension.sol";
import "./IVault.sol";

/// @title IUniswapSwapProxy
interface IUniswapSwapProxy {
    /// @notice This function is called by a user to initiate a token swap, its primary usage is for users who have no ETH wanting to purchase _toToken with the _fromToken they currently hold. This is possible through the sponsored transaction model and is described in detail in the readme.
    /// @dev The _gasRefund parameter is the total amount of ETH required for the sponsor TX, approval TX and swap TX. This function then swaps an arbitrary amount of _fromToken to recover this and the fee is transferred to the vault contract.
    /// @param _fromToken The token the user is swapping from
    /// @param _toToken The token the user is swapping into
    /// @param _amount The amount of _fromToken the user would like to swap
    /// @param _gasRefund The total amount of ETH that is required to refund the sponsored transaction
    /// @param _slippage The slippage used for the swaps
    function proxySwapWithFee(
        IERC20Extension _fromToken,
        IERC20Extension _toToken,
        uint256 _amount,
        uint256 _gasRefund,
        uint256 _slippage
    ) external;
}

contracts/Vault.sol.content:
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

import "./interfaces/IVault.sol";

/// @title Vault
/// @dev This contract will be used to hold fee revenue, it also provides some helper function to top-up registered HOT_WALLETS
contract Vault is AccessControlEnumerable, IVault {
    // The role used for HOT_WALLETs
    bytes32 public constant HOT_WALLET = keccak256("HOT_WALLET");
    bytes32 public constant HOT_WALLET_ADMIN = keccak256("HOT_WALLET_ADMIN");

    uint256 public walletBalanceLimit;

    constructor(address _admin) {
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);

        _setupRole(HOT_WALLET_ADMIN, _admin);
        _setRoleAdmin(HOT_WALLET, HOT_WALLET_ADMIN);
    }

    receive() external payable override {
        emit Received(msg.sender, msg.value);
    }

    function claimFees() external override onlyRole(DEFAULT_ADMIN_ROLE) {
        uint256 thisBalance = address(this).balance;
        payable(msg.sender).transfer(thisBalance);

        emit FeesClaimed(msg.sender, thisBalance);
    }

    function paidFees(address _sender, uint256 _amount)
        external
        payable
        override
    {
        require(msg.value >= _amount, "Not enough ETH sent");

        emit PaidFees(_sender, _amount);
    }

    function setWalletBalanceLimit(uint256 _walletBalanceLimit)
        external
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        walletBalanceLimit = _walletBalanceLimit;
    }

    function ethRequiredForHotWalletTopup()
        external
        view
        override
        returns (uint256 totalETH)
    {
        uint256 numHotWallets = getRoleMemberCount(HOT_WALLET);

        // Iterate over the hot wallets
        for (uint256 i = 0; i < numHotWallets; i++) {
            address payable hotWalletMember = payable(
                getRoleMember(HOT_WALLET, i)
            );

            uint256 hotWalletBalance = hotWalletMember.balance;

            // If the given wallet needs a top-up
            if (hotWalletBalance < walletBalanceLimit) {
                uint256 balanceDiff = walletBalanceLimit - hotWalletBalance;

                totalETH += balanceDiff;
            }
        }
    }

    function topUpHotWallets()
        external
        payable
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        uint256 numHotWallets = getRoleMemberCount(HOT_WALLET);

        require(numHotWallets > 0, "No Hot Wallets to fund");

        // Iterate over the hot wallets
        for (uint256 i = 0; i < numHotWallets; i++) {
            address payable hotWalletMember = payable(
                getRoleMember(HOT_WALLET, i)
            );

            uint256 hotWalletBalance = hotWalletMember.balance;

            // If the given wallet needs a top-up
            if (hotWalletBalance < walletBalanceLimit) {
                uint256 balanceDiff = walletBalanceLimit - hotWalletBalance;

                // Revert if not enough ETH, this will allow us to more easily gauge the required amount of ETH to top-up hot wallets
                require(
                    address(this).balance >= balanceDiff,
                    "Not enough ETH to top-up wallets"
                );

                hotWalletMember.transfer(balanceDiff);
            }
        }
    }
}

contracts/Whitelist.sol.content:
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./interfaces/IWhitelist.sol";

contract Whitelist is IWhitelist, AccessControlEnumerable {
    mapping(address => bool) whitelist;

    constructor(address _admin) {
        _setupRole(DEFAULT_ADMIN_ROLE, _admin);
    }

    function isWhitelisted(address _address)
        public
        view
        override
        returns (bool)
    {
        return whitelist[_address];
    }

    function addToWhitelist(address _address)
        public
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(
            _address != address(0),
            "Can't add the 0x address to the whitelist"
        );
        whitelist[_address] = true;

        emit AddedToWhitelist(_address);
    }

    function removeFromWhitelist(address _address)
        public
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        require(
            isWhitelisted(_address),
            "Address is missing from the whitelist"
        );
        delete whitelist[_address];

        emit RemovedFromWhitelist(_address);
    }
}

contracts/interfaces/IWhitelist.sol.content:
interface IWhitelist {
    /// @dev Event emitted whenever a new contractAddress is added to the whitelist
    event AddedToWhitelist(address indexed contractAddress);

    /// @dev Event emitted whenever a contractAddress is removed from the whitelist
    event RemovedFromWhitelist(address indexed contractAddress);

    /// @notice This function returns whether a given _address param is within the whitelist
    /// @param _address The address to return the whitelist status for
    /// @return A boolean indiciating if the address is whitelisted
    function isWhitelisted(address _address) external returns (bool);

    /// @notice This function is called by an owner to add a new contractAddress to the whitelist
    /// @param _address The new address to add the whitelist
    function addToWhitelist(address _address) external;

    /// @notice This function is called by an owner to remove a contractAddress from the whitelist
    /// @param _address The address to remove from the whitelist
    function removeFromWhitelist(address _address) external;
}

